import Przedmiotimport Grupaimport Salamodule Zajecia wheretype ID		=	Inttype Name	=	Stringtype Duration = Intdata TypZajec = Wyklad | Laboratorium | Projekt deriving (Enum)data Zajecia = Zajecia ID TypZajec Przedmiot Grupa Sala Durationinstance Show Zajecia where	show (Zajecia id typ przedmiot grupa sala dura) = "* Zajecia \t numer \t\t" ++ show id ++ "\n" ++								"\t ======================\n"								"\t nazwa\t\t" ++ show nam ++ "\n" ++								"\t przedmiot \t\t" ++ show przedmiot ++ "\n" ++								"\t grupa \t\t" ++ show grupa ++ "\n" ++								"\t sala \t\t" ++ show sala ++ "\n" ++								"\t czas trwania \t\t" ++ show dura ++ "\n" ++								"\t ======================\n"ZajeciaID :: Zajecia -> IDZajeciaID (Zajecia x _) = x								ZajeciaName :: Zajecia -> NameZajeciaName (Zajecia _ name ) = name