module Zajecia wheretype StartTime = Int--- data TypZajec = Wyklad | Laboratorium | Projekt deriving (Enum)-- Konstruktor Zajec-- ID -mozliwe ze nie bedzie potrzebne-- Przedmiot Grupa Sala - wiadomo-- Day - dzien zajec. Zakladamy ze jest 5 dni Pon-Pt. Tutaj uzywamy enuma z System.Time dla wygody-- StartTime - slot czasowy w ktorym  rozpoczynaja sie zajecia. Zakladamy np ze dzien ma zajecia od 8-20 tj. 12 slotow czasowychdata Zajecia = Zajecia ID Przedmiot Grupa Sala Day StartTimeinstance Show Zajecia where	show (Zajecia id przedmiot grupa sala dzien s_time) = "* Zajecia \t numer \t\t" ++ show id ++ "\n" ++ "\t ======================\n" ++								"\t przedmiot \t\t" ++ show przedmiot ++ "\n" ++								"\t grupa \t\t" ++ show grupa ++ "\n" ++								"\t sala \t\t" ++ show sala ++ "\n" ++								"\t dzien \t\t" ++ show dzien ++ "\n" ++								"\t dzien \t\t" ++ show s_time ++ "\n" ++								"\t ======================\n";zajeciaID :: Zajecia -> IDZajeciaID (Zajecia x _ _ _ _ _) = x								zajeciaPrzedmiot :: Zajecia -> PrzedmiotzajeciaPrzedmiot (Zajecia _ przedmiot _ _ _ _) = przedmiotzajeciaGrupa :: Zajecia -> GrupazajeciaGrupa (Zajecia _ _ g _ _ _) = gzajeciaSala :: Zajecia -> SalazajeciaSala (Zajecia _ _ _ s _ _) = szajeciaDzien :: Zajecia -> DayzajeciaDzien (Zajecia _ _ _ _ d _) = dzajeciaStartTime :: Zajecia -> StartTimezajeciaStartTime (Zajecia _ _ _ _ _ s_time) = s_time